#include <iostream>
using namespace std;

int x = 10;
int y = 9;
int z;

//Fazer um programa que converte de decimal para exodecimal e viseversa.


int main(){
    system("clear"); 
    printf("A minha variavel x tem o valor de %d e a y de %d\n", x, y);
    scanf("%z", &z);
    printf("A minha variavel z tem o valor de %d\n", z);
    return 0;
}